'use strict';

const mongo = require('mongodb');

const MongoClient = require('./mongo-client');
const Mongodb = require('./mongodb');

const client = (url, dbName) => new MongoClient(url, dbName);

const mongodb = (cli, collection) => new Mongodb(cli, collection);

module.exports = {
  client,
  mongodb,
  ObjectID: mongo.ObjectId,
};
